import { useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useDispatch, useSelector } from "react-redux";
import { uiActions } from "./components/redux/uiSlice";
import Notification from "./components/UI/Notification"

function App() {
  const dispatch = useDispatch();
  const cartIsShown = useSelector((state) => state.cart.cartIsVisible);
  const cart = useSelector((state) => state.cart.cart);
  const notification = useSelector(state => state.ui.notification)
  useEffect(() => {
    const sendCartData = async () => {
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending cart data!",
        })
      );
      const response = await fetch(
        "https://react-http-2e5c1-default-rtdb.europe-west1.firebasedatabasef.app/cart.json",
        { method: 'POST', body: JSON.stringify({cart}) }
      );
      if (!response.ok) {
        throw new Error("Sending cart data failed.");
      }
      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Data sent",
          message: "Data sucessfully sent!",
        })
      );
    };
    sendCartData().catch((error) => {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error occured",
          message: "Sending cart data failed.",
        })
      );
    });
  }, [dispatch, cart]);
  return (
    <>
    {notification && <Notification status={notification.status} title={notification.title} message={notification.message}/>}
    <Layout>
      {cartIsShown && <Cart />}
      <Products />
    </Layout>
    </>
  );
}

export default App;
